using System.Collections.Generic;
using CentralSecurityProject.Common;
using System.ComponentModel.DataAnnotations;
using System.ComponentModel.DataAnnotations.Schema;

namespace CentralSecurityProject.Models.Security
{
    /// <summary>
    /// کلاس مربوط به کاربران زیر سیستم های
    /// موجود در زیر سیستم جامع
    /// </summary>
    [Table("tbUser", Schema = "Security")] // Default Name : UserModels
    public class UserModel : SecurityBaseModel
    {
        #region Constructor(s)
        /// <summary>
        /// ایجاد یک سازنده پیش فرض
        /// </summary>
        public UserModel()
        {
            this.Role_List = new HashSet<RoleModel>();
        }

        /// <summary>
        /// ایجاد سازنده پیش فرض با مقدار دهی اولیه 
        /// </summary>
        /// <param name="userLogin">کلمه کاربری</param>
        /// <param name="username">نام کاربر</param>
        /// <param name="departement">دپارتمان</param>
        /// <param name="post">جایگاه</param>
        /// <param name="isactive">وضعیت</param>
        /// <param name="password">کلمه عبور</param>
        /// <param name="changepassatnextlogon">تغییر کلمه عبور</param>
        /// <param name="usercannotchangepass">عدم امکان تغییر کلمه عبور</param>
        public UserModel(string userLogin, string username, string departement, string post, bool isactive,
            string password, bool changepassatnextlogon, bool usercannotchangepass, string telephon)
        {
            this.Role_List = new HashSet<RoleModel>();

            UserLogin = userLogin;
            UserName = username;
            Departement = departement;
            Post = post;
            PersonalId = 123;
            IsActive = isactive;
            Password = password;
            OldPass = password;
            SetPassDate = System.DateTime.Now;
            ChangePassAtNextLogon = changepassatnextlogon;
            UserCanNotChangePass = usercannotchangepass;
            MaxPassAge = 65;
            MinPassAge = 30;
            MinPassLength = 8;
            PassMustComplexity = true;
            EnforcePassHistory = 2;
            PhotoFileName = "default.png";
            PhotoFileContent = System.Text.Encoding.Unicode.GetBytes("");
            Photo = (System.Web.HttpPostedFileBase)new MemoryPostedFile(PhotoFileContent);
            Telephon = telephon;
        }

        #endregion

        #region Property(s)
        /// <summary>
        /// شناسه کاربر
        /// </summary>
        [Key]
        [MyRequired]
        [Display(Name = "شناسه کاربر", Order = 1, Description = "شناسه کاربر")]
        [Column("UserId", Order = 1)]
        public int UserId { get; set; }
        public override int ID
        {
            get
            {
                return UserId;
            }
        }

        /// <summary>
        /// کد کاربری / کاربر ورودی
        /// </summary>
        [MyRequired]
        [Display(Name = "کد کاربری", Order = 2, Description = "کد کاربری/کاربر ورودی")]
        [Column("UserLogin", Order = 2, TypeName = "nvarchar")]
        [MyMaxLength(20)] // MyMaxLength Attribute replacement for MaxLength Attribute
        public string UserLogin { get; set; }

        /// <summary>
        /// نام کاربر ورودی به سیستم
        /// </summary>
        [MyRequired]
        [Display(Name = "نام کاربری", Order = 3, Description = "نام کاربر ورودی به سیستم")]
        [Column("UserName", Order = 3, TypeName = "nvarchar")]
        [MyMaxLength(80)]
        public string UserName { get; set; }

        /// <summary>
        /// فیلد اطلاعاتی کد و نام کاربری
        /// </summary>
        [NotMapped]
        [Display(Name = "عنوان کاربر", Order = 4, Description = "عنوان کاربر")]
        public string DisplayUserName
        {
            get
            {
                return $"{UserLogin} - {UserName}";
            }
        }

        /// <summary>
        /// جایگاه ساختار سازمانی
        /// </summary>
        [MyRequired]
        [Display(Name = "دپارتمان", Order = 5, Description = "جایگاه ساختار سازمانی")]
        [Column("Departement", Order = 5, TypeName = "nvarchar")]
        [MyMaxLength(100)]
        public string Departement { get; set; }

        /// <summary>
        /// پست سازمانی
        /// </summary>
        [MyRequired]
        [Display(Name = "پست سازمانی", Order = 6, Description = "پست سازمانی")]
        [Column("Post", Order = 6, TypeName = "nvarchar")]
        [MyMaxLength(80)]
        public string Post { get; set; }

        /// <summary>
        /// شماره تماس تلفنی
        /// </summary>
        [MyRequired]
        [Display(Name = "شماره تلفن", Order = 7, Description = "شماره تماس تلفنی")]
        [Column("Telephon", Order = 7, TypeName = "nvarchar")]
        [MyMaxLength(15)]
        public string Telephon { get; set; }

        /// <summary>
        /// شماره شناسه پرسنلی
        /// </summary>
        [MyRequired]
        [Display(Name = "شناسه پرسنلی", Order = 8, Description = "شناسه پرسنلی")]
        [Column("PersonalId", Order = 8, TypeName = "int")]
        public int PersonalId { get; set; }

        /// <summary>
        /// وضعیت فعال/غیر فعال
        /// کاربر استفاده کننده از سیستم
        /// </summary>
        [MyRequired]
        [Display(Name = "وضعیت", Order = 9, Description = "وضعیت فعال/غیر فعال")]
        [Column("IsActive", Order = 9, TypeName = "bit")]
        public bool IsActive { get; set; }

        /// <summary>
        /// کلمه عبور کاربر
        /// استفاده کننده از سیستم
        /// </summary>
        [MyRequired]
        [Display(Name = "کلمه عبور", Order = 10, Description = "کلمه عبور کاربر")]
        [Column("Password", Order = 10, TypeName = "nvarchar")]
        [MyMaxLength(100)]
        public string Password { get; set; }

        /// <summary>
        /// کلمه قدیمی عبور کاربر
        /// استفاده کننده از سیستم
        /// </summary>
        [MyRequired]
        [Display(Name = "کلمه قدیمی عبور", Order = 11, Description = "کلمه قدیمی عبور کاربر")]
        [Column("OldPass", Order = 11, TypeName = "nvarchar")]
        [MyMaxLength(100)]
        public string OldPass { get; set; }

        /// <summary>
        /// تاریخ تنظیم کلمه عبور
        /// توسط کاربر استفاده کننده از سیستم
        /// </summary>
        //[MyRequired]
        //[Range(typeof(System.DateTime), "1/2/2004", "3/4/2004", ErrorMessage = "Value for {0} must be between {1} and {2}")]
        [Display(Name = "تاریخ تنظیم کلمه عبور", Order = 12, Description = "تاریخ تنظیم کلمه عبور")]
        [Column("SetPassDate", Order = 12, TypeName = "smalldatetime")]
        public System.DateTime? SetPassDate { get; set; }

        /// <summary>
        /// تغییر کلمه عبور در ورود بعدی
        /// </summary>
        [MyRequired]
        [Display(Name = "تغییر کلمه عبور در ورود بعدی", Order = 13, Description = "تغییر کلمه عبور در ورود بعدی")]
        [Column("ChangePassAtNextLogon", Order = 13, TypeName = "bit")]
        public bool ChangePassAtNextLogon { get; set; }

        /// <summary>
        /// امکان تغییر کلمه عبور
        /// توسط کاربر سیستم
        /// </summary>
        [MyRequired]
        [Display(Name = "امکان تغییر کلمه عبور", Order = 14, Description = "امکان تغییر کلمه عبور : بله / خیر")]
        [Column("UserCanNotChangePass", Order = 14, TypeName = "bit")]
        public bool UserCanNotChangePass { get; set; }

        /// <summary>
        /// حداکثر طول عمر کلمه عبور
        /// </summary>
        [MyRequired]
        [Display(Name = "حداکثر طول عمر کلمه عبور", Order = 15, Description = "حداکثر طول عمر کلمه عبور")]
        [Column("MaxPassAge", Order = 15, TypeName = "smallint")]
        [MyRange(30, 90)]
        public System.Int16 MaxPassAge { get; set; }

        /// <summary>
        /// حداقل طول عمر کلمه عبور
        /// </summary>
        [MyRequired]
        [Display(Name = "حداقل طول عمر کلمه عبور", Order = 16, Description = "حداقل طول عمر کلمه عبور")]
        [Column("MinPassAge", Order = 16, TypeName = "smallint")]
        [MyRange(7, 30)]
        public System.Int16 MinPassAge { get; set; }

        /// <summary>
        /// حداقل طول کلمه عبور
        /// </summary>
        [MyRequired]
        [Display(Name = "حداقل طول کلمه عبور", Order = 17, Description = "حداقل طول کلمه عبور")]
        [Column("MinPassLength", Order = 17, TypeName = "tinyint")]
        public byte MinPassLength { get; set; }

        /// <summary>
        /// پیچیدگی اجباری در کلمه عبور
        /// </summary>
        [MyRequired]
        [Display(Name = "پیچیدگی اجباری در کلمه عبور", Order = 18, Description = "پیچیدگی اجباری در کلمه عبور می بایست وجود داشته باشد یا خیر")]
        [Column("PassMustComplexity", Order = 18, TypeName = "bit")]
        public bool PassMustComplexity { get; set; }

        /// <summary>
        /// اجبار تغییر در کلمه عبور
        /// </summary>
        [MyRequired]
        [Display(Name = "اجبار تغییر در کلمه عبور", Order = 19, Description = "اجبار تغییر در کلمه عبور")]
        [Column("EnforcePassHistory", Order = 19, TypeName = "tinyint")]
        public byte EnforcePassHistory { get; set; }

        [MyMaxLength(100)]
        [Display(Name = "عنوان فایل تصویر", Order = 20, Description = "عنوان فایل تصویری بارگذاری شده")]
        public string PhotoFileName { get; set; }

        [Display(Name = "محتوای فایل تصویر", Order = 21, Description = "محتوای فایل تصویری بارگذاری شده")]
        public byte[] PhotoFileContent { get; set; }  // نکته : سمت کلاینت آرایه بایت قابل شناسایی نمی باشد و به همین دلیل می بایست خصوصیت زیر را تعریف کرد

        [MyRequired]
        [NotMapped] // تعریف این خصوصیت به این دلیل است که چنین داده ای در سمت بانک اطلاعاتی قابل پیاده سازی نمی باشد
        [Display(Name = "تصویر کاربر", Order = 22, Description = "تصویر کاربر بارگذاری شده")]
        public System.Web.HttpPostedFileBase Photo { get; set; }

        [Index("UK_tbUser_AspNetUserId", IsUnique = true)]
        [Column("AspNetUserId", Order = 23, TypeName = "nvarchar")]
        [MaxLength(128)]
        [Display(Name = "کاربر ورودی", Order = 23, Description = "کاربر ورودی")]
        public string AspNetUserId { get; set; }

        public virtual ApplicationUser User_AspNetUser { get; set; }

        #endregion

        #region Collection(s)
        /// <summary>
        /// لیست رابطه بین کاربر و زیر سیستم که
        /// برای کاربر فوق تعریف شده است
        /// </summary>
        public virtual System.Collections.Generic.ICollection<AppUserModel> AppUser_List { get; set; }

        /// <summary>
        /// لیست نقش ها
        /// </summary>
        public virtual System.Collections.Generic.ICollection<RoleModel> Role_List { get; set; }

        /// <summary>
        /// لیست درخواست ها
        /// </summary>
        public virtual System.Collections.Generic.ICollection<Security.RequestModel> Request_List { get; set; }

        #endregion

        #region Configuration(s)
        // نکته : در جاهایی که می خواهیم تنظیماتی برای آن مدل داشته باشیم بهتر است
        // که آن را بصورت یک کلاس داخلی در همان کلاس مربوطه تعریف کنیم

        /// <summary>
        /// کلاس داخلی تنظیمات جدول
        /// کاربران سیستم
        /// </summary>
        internal class Configuration : System.Data.Entity.ModelConfiguration.EntityTypeConfiguration<UserModel>
        {
            public Configuration()
            {
                this.ToTable("tbUser", "Security");

                //IndexAttribute index = new IndexAttribute("UK_tbUser_AspNetUserId");
                //index.IsUnique = true;
                //HasTableAnnotation("Index", index);

                HasMany(x => x.AppUser_List).
                    WithRequired(x => x.User).
                    WillCascadeOnDelete(false);

                HasMany(x => x.Request_List).
                    WithOptional(x => x.Expert).
                    WillCascadeOnDelete(false);
            }
        }

        #endregion
    }
}